import React from 'react'
import {connect} from 'react-redux'
import Layout from '../components/MyLayout'
import Page from '../components/Page'
import {tickClock, startClock} from '../action/clockAction'
class Clock extends React.Component {
    static async getInitialProps(props) {
        const {store, isServer} = props.ctx
        store.dispatch(tickClock(isServer))
        // store.dispatch(startClock())
        // const {dataSaga} = store.getState().clockReduder
        console.log('Clock:',store.getState().clockReduder);
        
        return {isServer}
    }
    componentDidMount(){
        this.props.dispatch(startClock())
        // console.log('clock-did--mount', this.props);
    }
    render(){
        return (
            <Layout>
                <Page />
            </Layout>
        )
    }
}
export default connect()(Clock)
