import * as types from '../action/types'

import {tickClock} from '../action/clockAction'

function* runClockSaga(){
    yield take(types.START_CLOCK)
    while(true) {
        yield put(tickClock(true))
        yield delay(1000)
    }
}
export default runClockSaga
