import React from 'react'
import { connect } from 'react-redux'
import Layout from '../components/MyLayout.js'
import styled from "styled-components";
import withReduxSaga from '../lib/withReduxSaga'
// actions
import { getUser } from '../action/testAction'
const Bound = styled.div`
    display: flex;
    flex: 1;
    /* background: pink; */
    flex-direction: column;
    .main-table-user{

    }
    .head-table{
        display: grid;
        grid-template-columns: 10% 40% 25% 25%;
    }
    .body-table{
        .block-row{
            display: grid;
            grid-template-columns: 10% 40% 25% 25%;
        }
    }
`;

class MainUsers extends React.Component {
    static async getInitialProps({ ctx: { store } }) {
        console.log('getInitialProps: ', store);
        // store.dispatch(getUser())
    }
    componentDidMount() {
        this.props.getUser()
        console.log('Did');
    }
    componentWillReceiveProps(n){
        console.log(n);
        
    }
    render() {
        return (
            <Layout>
                <Bound>
                    <p>Users Page.</p>
                    <div className='main-table-user'>
                        <div className='head-table'>
                            <div className='headitem'>STT</div>
                            <div className='headitem'>Full Name</div>
                            <div className='headitem'>Job</div>
                            <div className='headitem'>Phone</div>
                        </div>
                        <div className='body-table'>
                            <div className='block-row'>
                                <div className='humenitem'>1</div>
                                <div className='humenitem'>2</div>
                                <div className='humenitem'>3</div>
                                <div className='humenitem'>4</div>
                            </div>
                        </div>
                    </div>
                </Bound>
            </Layout>
        )
    }
}
const mapDispatchToProps = dispatch => ({  // phát đi tất cả những action (or tín hiệu) khi loadImages được gọi
    getUser: () => dispatch(getUser()),
})

const mapStateToProps = (state) => {
    // console.log('mapStateToProps', state);
    
    return {
        usersReducer: state.testReducer
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(MainUsers)
